create database if not exists your_db;

create table your_db.user
(
    id          bigint auto_increment comment '主键'
        primary key,
    username    varchar(128) default '新用户'          not null comment '用户名',
    account     varchar(128) default 'account'         not null comment '账号',
    password    varchar(128) default '12345678'        not null comment '密码',
    avatar_url  varchar(512) default ''                not null comment '用户头像',
    gender      tinyint      default 0                 not null comment '性别（0-未设置，1-男，2-女）',
    tags        varchar(512) default '[]'              null comment '标签',
    role        tinyint      default 0                 not null comment '角色（0-普通用户，1-管理员）',
    create_time timestamp    default CURRENT_TIMESTAMP not null comment '创建时间',
    update_time timestamp    default CURRENT_TIMESTAMP not null on update CURRENT_TIMESTAMP comment '更新时间',
    deleted     tinyint      default 0                 not null comment '是否删除（0-未删除，1-已删除）'
)
    comment '用户表';

